// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;
import '../storeContract/DataStorage.sol';
import '../math/SafeMathuint.sol';

contract RatePlatform is DataStorage{
    using SafeMathuint for uint16;

    uint16 constant RATE_INTERVAL_PLATFORM = 100;
    /* need to make overflow about rate */
    function getRatePlat() public view returns (uint16) {
        return platforms[msg.sender].rate;
    }

    function incrementRatePlat(address targetAddress) internal {
        uint16 rate = platforms[targetAddress].rate;
        uint16 c = rate + RATE_INTERVAL_PLATFORM;
        if (c < rate)
            platforms[targetAddress].rate = c;
        else
            platforms[targetAddress].rate = MAX_RATE_PLATFORM;
    }

    function decreaseRatePlat(address targetAddress) internal {
        uint16 rate = inspectors[targetAddress].rate;
        uint16 c = rate - RATE_INTERVAL_PLATFORM;

        if (rate > c)
            platforms[targetAddress].rate = c;
        else
            platforms[targetAddress].rate = 0;
    }

    function decideKeepRolePlat(address _target) internal returns (bool){
        require(platforms[msg.sender].ablePlatform == true, "Not registered Inspector");
        if (platforms[_target].rate <= MIN_RATE_PLATFORM) {
            platforms[_target].ablePlatform = false;
            return false;
        }
        return true;
    }
}
